// ===== frontend/src/components/Navbar.js =====
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
export default function Navbar(){
const nav = useNavigate();
const raw = localStorage.getItem('user');
const u = raw ? JSON.parse(raw) : null;
function logout(){ localStorage.removeItem('user'); nav('/login'); }

 let user = null;
  try {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      user = JSON.parse(storedUser);
    }
  } catch (err) {
    console.warn("Invalid user in localStorage");
  }

;


return (
<nav style={{display:'flex',gap:16,padding:12,borderBottom:'1px solid #ddd'}}>
<Link to="/">Home</Link>
<Link to="/book">Public Booking</Link>
{!u && <Link to="/login">Login</Link>}
{u && <>
{u.user.role==='Admin' && <Link to="/admin">Admin</Link>}
{u.user.role==='Doctor' && <Link to="/doctor">Doctor</Link>}
{u.user.role==='Staff' && <Link to="/staff">Staff</Link>}
<button onClick={logout}>Logout</button>
</>}
</nav>
);
}
