// ===== frontend/src/pages/doctor/DoctorDashboard.js =====
import { API, authHeader } from '../../api';


function DayRow({ day, value, onChange }){
const [rows, setRows] = useState(value?.windows?.length ? value.windows : [{start:'09:00', end:'12:00'}]);
useEffect(()=>{ onChange({ day, windows: rows }); },[rows]);
return (
<div style={{border:'1px solid #eee', padding:8, marginBottom:6}}>
<b>{['Sun','Mon','Tue','Wed','Thu','Fri','Sat'][day]}</b>
{rows.map((r,i)=>(
<div key={i} style={{display:'flex', gap:8, alignItems:'center'}}>
<input value={r.start} onChange={e=>{ const v=[...rows]; v[i]={...v[i], start:e.target.value}; setRows(v);} }/>
<span>to</span>
<input value={r.end} onChange={e=>{ const v=[...rows]; v[i]={...v[i], end:e.target.value}; setRows(v);} }/>
<button onClick={()=>setRows(rows.filter((_,x)=>x!==i))}>remove</button>
</div>
))}
<button onClick={()=>setRows([...rows,{start:'14:00', end:'17:00'}])}>+ window</button>
</div>
);
}


export default function DoctorDashboard(){
const [profile,setProfile] = useState(null);
const [availability,setAvailability] = useState(Array(7).fill(null).map((_,i)=>({day:i, windows:[]})));
const [capacity,setCapacity] = useState(1);
const [buffer,setBuffer] = useState(0);
const [appts,setAppts] = useState([]);


async function load(){
const me = await axios.get(API + '/api/doctors/me', { headers: authHeader()});
setProfile(me.data); setCapacity(me.data.capacityPerSlot||1); setBuffer(me.data.bufferMinutes||0);
const base = Array(7).fill(null).map((_,i)=>({day:i, windows:[]}));
const wa = me.data.weeklyAvailability?.length ? me.data.weeklyAvailability : base;
setAvailability(wa);
const list = await axios.get(API + '/api/appointments/mine', { headers: authHeader()});
setAppts(list.data);
}
useEffect(()=>{ load(); },[]);


async function save(){
await axios.put(API + '/api/doctors/availability', { weeklyAvailability: availability, capacityPerSlot: capacity, bufferMinutes: buffer }, { headers: authHeader()});
load();
}


return (
<div style={{padding:24, display:'grid', gap:16}}>
<h2>Doctor Dashboard</h2>
<div>Welcome, {profile?.user?.name}</div>
<div style={{display:'grid',gridTemplateColumns:'1fr 1fr', gap:16}}>
<section>
<h3>Weekly Availability</h3>
<label>Capacity per 30m slot <input type="number" value={capacity} onChange={e=>setCapacity(+e.target.value)}/></label>
<label>Buffer minutes between slots <input type="number" value={buffer} onChange={e=>setBuffer(+e.target.value)}/></label>
{availability.map((d,i)=> <DayRow key={i} day={i} value={d} onChange={row=>{
const v=[...availability]; v[i]=row; setAvailability(v);
}}/>) }
<button onClick={save}>Save Availability</button>
</section>
<section>
<h3>My Appointments</h3>
<ul>
{appts.map(a=> <li key={a._id}>{a.date} {a.slotStart}-{a.slotEnd} â€” {a.patientName} ({a.patientPhone})</li>)}
</ul>
</section>
</div>
</div>
);
}
