// ===== frontend/src/pages/staff/StaffDashboard.js =====
import React, { useEffect, useState } from "react";
import axios from "axios";
import { API, authHeader } from "../../api";
import "./StaffDashboard.css";   // ‚úÖ Import CSS

export default function StaffDashboard() {
  const [list, setList] = useState([]);

  async function load() {
    const { data } = await axios.get(API + "/api/bookings", {
      headers: {
        ...authHeader(),
        "Cache-Control": "no-cache",
        "Pragma": "no-cache",
      },

      
    });
    

    // üîπ Optional filtering for assigned doctors (if stored in user object)
    const raw = localStorage.getItem("user");
    let doctorIds = [];
    if (raw) {
      const u = JSON.parse(raw);
      doctorIds = u.assignedDoctors || [];
    }

    let filtered = data;
    if (doctorIds.length > 0) {
      filtered = data.filter((b) => doctorIds.includes(b.doctor?._id));
    }

    setList(filtered);
  }

  useEffect(() => {
    load();
  }, []);

  return (
    <div className="staff-dashboard">
      <h2>Staff Dashboard</h2>
      <ul className="booking-list">
        {list.map((b) => (
          <li key={b._id} className="booking-card">
            <div className="booking-date">
              üìÖ {b.date} ‚Äî ‚è∞ {b.time}
            </div>
            <div className="booking-details">
              <strong>Doctor:</strong> {b.doctor?.name || "Unassigned"}
            </div>
            <div className="booking-details">
              <strong>Patient:</strong> {b.name} ({b.phone})
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
