// ===== frontend/src/pages/Login.js =====
import React, { useState } from 'react';
import axios from 'axios';
import { API } from '../api';
import { useNavigate } from 'react-router-dom';


export default function Login(){

const res =  axios.post(API + "/api/auth/login", { email, password, role });
localStorage.setItem("token", res.data.token);   // must be here
localStorage.setItem("user", JSON.stringify(res.data.user));


const [email,setEmail] = useState('admin@opd.local');
const [password,setPassword] = useState('admin123');
const nav = useNavigate();
async function onSubmit(e){
e.preventDefault();
const { data } = await axios.post(API + '/api/auth/login', { email, password });
localStorage.setItem('user', JSON.stringify(data));
const role = data.user.role;
if(role==='Admin') nav('/admin');
else if(role==='Doctor') nav('/doctor');
else nav('/staff');
}
return (
<form onSubmit={onSubmit} style={{padding:24,display:'grid',gap:8,maxWidth:320}}>
<h2>Login</h2>
<input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)}/>
<input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)}/>
<button>Login</button>
<p>Default admin gets seeded from backend .env</p>
</form>
);
}
