// ===== frontend/src/pages/staff/StaffDashboard.js =====
import React, { useEffect, useState } from "react";
import axios from "axios";
import { API, authHeader } from "../../api";

export default function StaffDashboard() {
  const [list, setList] = useState([]);

  async function load() {
    const { data } = await axios.get(API + "/api/bookings", {
      headers: {
        ...authHeader(),
        "Cache-Control": "no-cache",
        "Pragma": "no-cache",
      },
    });

    // ðŸ”¹ Optional filtering for assigned doctors (if stored in user object)
    const raw = localStorage.getItem("user");
    let doctorIds = [];
    if (raw) {
      const u = JSON.parse(raw);
      doctorIds = u.assignedDoctors || [];
    }

    let filtered = data;
    if (doctorIds.length > 0) {
      filtered = data.filter((b) => doctorIds.includes(b.doctor?._id));
    }

    setList(filtered);
  }

  useEffect(() => {
    load();
  }, []);

  return (
    <div style={{ padding: 24 }}>
      <h2>Staff Dashboard</h2>
      <ul>
        {list.map((b) => (
          <li key={b._id}>
            {b.date} {b.time} â€” {b.doctor?.name} â€” {b.name} ({b.phone})
          </li>
        ))}
      </ul>
    </div>
  );
}
