// ===== frontend/src/pages/staff/StaffDashboard.js =====
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { API, authHeader } from '../../api';


export default function StaffDashboard(){
const [date,setDate] = useState('');
const [list,setList] = useState([]);
async function load(){
const { data } = await axios.get(API + '/api/appointments/mine' + (date?`?date=${date}`:''), { headers: authHeader()});
setList(data);
}
useEffect(()=>{ load(); },[date]);
return (
<div style={{padding:24}}>
<h2>Staff Dashboard</h2>
<label>Date filter <input type="date" value={date} onChange={e=>setDate(e.target.value)}/></label>
<ul>
{list.map(a=> <li key={a._id}>{a.date} {a.slotStart}-{a.slotEnd} — {a.doctor?.user?.name} — {a.patientName}</li>)}
</ul>
</div>
);
}
