// ===== frontend/src/components/Navbar.js =====
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function Navbar() {
  const nav = useNavigate();
  const raw = localStorage.getItem('user');
  const u = raw ? JSON.parse(raw) : null;

  function logout() {
    localStorage.removeItem('user');
    localStorage.removeItem('token'); // also clear token
    nav('/login');
  }

  return (
    <nav style={{ display: 'flex', gap: 16, padding: 12, borderBottom: '1px solid #ddd' }}>
      <Link to="/">Home</Link>
      <Link to="/book">Public Booking</Link>

      {!u && <Link to="/login">Login</Link>}

      {u && (
        <>
          {u.role === 'Admin' && <Link to="/admin">Admin</Link>}
          {u.role === 'Doctor' && <Link to="/doctor">Doctor</Link>}
          {u.role === 'Staff' && <Link to="/staff">Staff</Link>}

          <span style={{ marginLeft: 'auto', fontStyle: 'italic' }}>
            {u.name || u.email}
          </span>

          <button onClick={logout}>Logout</button>
        </>
      )}
    </nav>
  );
}
